import "./style.css";
import React, { useState } from "react";
import jsPDF from "jspdf";

function App() {
  const [images, setImages] = useState([]);
  const doc = new jsPDF();

  const handleImageChange = (event) => {
    const file = event.target.files[0];
    const reader = new FileReader();

    reader.onload = (e) => {
      setImages((prevImages) => [...prevImages, e.target.result]); // Append new image
    };
    reader.readAsDataURL(file);
  };

  const handleGeneratePDF = () => {
    if (images.length == 0) {
      return;
    }
    images.forEach((image, index) => {
      if (index > 0) {
        doc.addPage([image.imgHeight * pxToMm, image.imgWidth * pxToMm]);
      } else {
        doc.internal.pageSize.width = image.imgHeight * pxToMm;
        doc.internal.pageSize.height = imgHeightMm;
      }
      doc.addImage(img, "PNG", 0, 0, imgWidth, imgHeight); // Full-page image
    });
    doc.save("image.pdf");
  };

  return (
    <>
      <div className="title">Join Images to PDF</div>

      <div className="upload">
        <div>Upload</div>
        <div>
          <input type="file" accept="image/*" onChange={handleImageChange} />
          <button onClick={handleGeneratePDF}>Generate PDF</button>
        </div>
      </div>
    </>
  );
}

export default App;
