import "./style.css";
import React, { useState } from "react";
import jsPDF from "jspdf";

const pxToMm = 0.264583; // Conversion from pixels to mm

function App() {
  const [images, setImages] = useState([]);
  const doc = new jsPDF();
  doc.deletePage(1);

  const handleImageChange = (event) => {
    const file = event.target.files[0];
    const reader = new FileReader();

    reader.onload = (e) => {
      setImages((prevImages) => [...prevImages, e.target.result]); // Append new image
    };
    reader.readAsDataURL(file);
  };

  const handleGeneratePDF = () => {
    let numPagesDone = 0;

    if (images.length == 0) {
      return;
    }
    images.forEach((image, index) => {
      const imageObject = new Image();
      imageObject.src = image;

      imageObject.onload = () => {
        console.log(imageObject.width);
        doc.addPage([imageObject.width * pxToMm, imageObject.height * pxToMm]);

        doc.addImage(
          image,
          "PNG",
          0,
          0,
          imageObject.width * pxToMm,
          imageObject.height * pxToMm
        );
        numPagesDone++;

        if (numPagesDone == images.length) {
          doc.save("merged.pdf");
        }
      };
    });
  };

  return (
    <>
      <div className="title">Join Images to PDF</div>

      <div className="upload">
        <div>Upload</div>
        <div>
          <input type="file" accept="image/*" onChange={handleImageChange} />
          <button className="generatePDFButton" onClick={handleGeneratePDF}>
            Generate PDF
          </button>
        </div>
        <div className="fileList">
          <div>File List</div>
        </div>
      </div>
    </>
  );
}

export default App;
